sp_angular_provider.script:
function delitemModalPubSub($rootScope) {

	var delitemModalEvent = 'modalCompleted';

	return {

		subscribe: function(scope, callback) {

			var deregister = $rootScope.$on(delitemModalEvent, callback);
			scope.$on('$destroy', deregister);

		},

		publish: function(data) {
			$rootScope.$emit(delitemModalEvent, data);

		}

	};

}
