sp_angular_provider.script:
function delitemDataTablePubSub($rootScope) {

	var delitemDataTableEvent = 'dataTableCompleted';

	return {

		subscribe: function(scope, callback) {

			var deregister = $rootScope.$on(delitemDataTableEvent, callback);
			scope.$on('$destroy', deregister);

		},

		publish: function(data) {
			$rootScope.$emit(delitemDataTableEvent, data);

		}

	};

}
