sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.options = input;

	data.delieveryItemTable = DelItemConstants.DELIVERY_ITEM_TABLE;
	data.newState = DelItemConstants.NEW;
	data.expectedState = DelItemConstants.EXPECTED;
	data.awaitingCollectionState = DelItemConstants.AWAITING_COLLECTION;
	data.collectedState = DelItemConstants.COLLECTED;
	data.cancelledState = DelItemConstants.CANCELLED;

	data.recipientFilter = "sys_id=" + data.options.delivery.recipient.value;
	data.recipientDisplay = data.options.delivery.recipient.display_value;	

	data.stateDisplay = "Awaiting Collection";//data.options.delivery.state.value;
	data.stateDefault = {

		id: data.options.delivery.state.id, 
		name: data.options.delivery.state.label

	}

	data.stateChoicesArr = [];
	var stateChoicesObj = {};

	var stateQuery = "name=" + data.delieveryItemTable + "^element=state";

	switch(data.options.delivery.state.value) {

		case "1":

			stateQuery += "^value!=3^value!=4";
			break;

		case "2":

			stateQuery += "^value!=1^value!=3^value!=4";
			break;

		case "3":

			stateQuery += "^value!=1^value!=2^value!=4";
			break;

	}

	var grSysChoice = new GlideRecordSecure('sys_choice');
	var isQueryValid = grSysChoice.isValidEncodedQuery(stateQuery);

	if(isQueryValid) {

		grSysChoice.addEncodedQuery(stateQuery);
		grSysChoice.orderBy('value')
		grSysChoice.query();
		while (grSysChoice.next()) {

			stateChoicesObj = {};
			stateChoicesObj.label = grSysChoice.label.toString();
			stateChoicesObj.value = grSysChoice.value.toString();
			data.stateChoicesArr.push(stateChoicesObj);

		}

	}

	//var collectionLocationsStr = gs.getProperty("x_187394_delitem.collection.locations");
	//data.collectionLocationsArr = collectionLocationsStr.split(",");
	data.collectionLocationsArr = new x_187394_delitem.DelitemDataUtils().retrieveCollectionLocations();
	/*var collectionLocationsObj = {};

	var collectionLocationGRS = new GlideRecordSecure('sys_choice');
	collectionLocationGRS.addEncodedQuery("name=" + DelItemConstants.DELIVERY_ITEM_TABLE +  "^element=collection_location");
	collectionLocationGRS.query();
	while (collectionLocationGRS.next()) {

		collectionLocationsObj = {};
		collectionLocationsObj.display = collectionLocationGRS.label.toString();
		collectionLocationsObj.value = collectionLocationGRS.value.toString();
		data.collectionLocationsArr.push(collectionLocationsObj);

	}*/

})();
