sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(input && input.action == "lookupDelivery") {

		try {

			var lookupDeliveryGRS = new GlideRecordSecure(DelItemConstants.DELIVERY_TABLE);
			lookupDeliveryGRS.addEncodedQuery("state!=3^recipient=" + input.recipient.value);
			lookupDeliveryGRS.query();
			if(lookupDeliveryGRS.next()) {
				gs.addInfoMessage(lookupDeliveryGRS.delivery_items_awaiting_collection);
				//gs.addInfoMessage(lookupDeliveryGRS.recipient.first_name);
			data.deliverySummaryOptions = {

						"deliverySysId": lookupDeliveryGRS.getUniqueValue(),
						"deliveryItems": lookupDeliveryGRS.delivery_items_awaiting_collection.toString(),
						"recipientFirstName": lookupDeliveryGRS.recipient.first_name.toString()

					};

			}


		} catch(err) {

			gs.error('['+gs.getCurrentScopeName()+']' + ': Delitem Self Service Widget - Caught error: \n'+err);

		}

	}

})();
