sp_widget.client_script:
api.controller=function($scope,delitemModalPubSub) {
	/* widget controller */
	var c = this;

	c.date = moment(new Date()).format('dddd Do MMMM YYYY');

	c.done = function(){	

		var modalReturnObj = {

			"modalAction" : "signatureAccepted",
			"signature" : c.accept()

		}

		//c.data.signature = c.accept();
		delitemModalPubSub.publish(modalReturnObj);

	}

};

