    multipleDeliveryObj.recipient.value = recipientStr;

    var createMultipleDeliveries = new x_187394_delitem.DelitemUtils().addMultipleDeliveryItem(multipleDeliveryObj);

})(parm_input, parm_variable, actionResult);
