        deliveryObj.collection_location = parm_input['new_collection_location'];

        deliveryObj.recipient.value = parm_input['recipient'];

        var createDelivery = new x_187394_delitem.DelitemUtils().addDeliveryItem(deliveryObj);

    } catch (err) {

        gs.error('[' + gs.getCurrentScopeName() + ']' + ': Add NEw Delivery Action Item - Caught error: \n' + err);

    }

})(parm_input, parm_variable, actionResult);
