sysauto_script.condition:
var dailyReminderTime = gs.getProperty("x_187394_delitem.daily.reminder");

if(dailyReminderTime) {

	true;

} else {

	false;

}

sysauto_script.script:
try {

	var outstandingDeliveryArr = [];

	var delivryReminderGRS = new GlideRecordSecure(DelItemConstants.DELIVERY_TABLE);
	delivryReminderGRS.addEncodedQuery("state!=3");
	delivryReminderGRS.query();
	while(delivryReminderGRS.next()) {

		outstandingDeliveryArr.push(delivryReminderGRS.getUniqueValue());

	}

	if(outstandingDeliveryArr.length > 0) {

		var scheduledDailyReminderGDT = new GlideDateTime(new GlideDateTime().getLocalDate() + ' ' + gs.getProperty("x_187394_delitem.daily.reminder"));

		gs.eventQueueScheduled("x_187394_delitem.daily.reminder", current, outstandingDeliveryArr, "", scheduledDailyReminderGDT);

	}

} catch(err) {

	gs.error('['+gs.getCurrentScopeName()+']' + ': Daily Reminder Scheduled Script - Caught error: \n'+err);

}
