sysauto_script.condition:
var dailyReminderTime = gs.getProperty("x_187394_delitem.expected.delivery.chase.time");

if(dailyReminderTime) {

	true;

} else {

	false;

}

sysauto_script.script:
try {

	var expectedDeliveryArr = [];

	var expectedDeliveryGRS = new GlideRecordSecure(DelItemConstants.DELIVERY_ITEM_TABLE);
	expectedDeliveryGRS.addEncodedQuery("state=" + DelItemConstants.EXPECTED);
	expectedDeliveryGRS.query();
	while(expectedDeliveryGRS.next()) {

		expectedDeliveryArr.push(expectedDeliveryGRS.getUniqueValue());

	}

	if(expectedDeliveryArr.length > 0) {

		var scheduledExpectedChaseGDT = new GlideDateTime(new GlideDateTime().getLocalDate() + ' ' + gs.getProperty("x_187394_delitem.expected.delivery.chase.time"));

		gs.eventQueueScheduled("x_187394_delitem.expected.delivery.chase", current, expectedDeliveryArr, "", scheduledExpectedChaseGDT);

	}

} catch(err) {

	gs.error('['+gs.getCurrentScopeName()+']' + ': Expected Delivery Chase Scheduled Script - Caught error: \n'+err);

}
